public static class SynonymSystem 
{
    private static TList<SynonymWord> SynonymWords
    {
        get
        {
            if (_SynonymWords == null)
            {
                
                if (Application.isEditor) PlayerPrefs.SetString("SynonymWordsSaver", "");
                if (string.IsNullOrEmpty(PlayerPrefs.GetString("SynonymWordsSaver")))
                {
                    PlayerPrefs.SetString("SynonymWordsSaver", ProjectSettings.ProjectSettings.Mine.DefaultSynonymWords.text);
                }
                _SynonymWords = JsonHelper.FromJsonList<SynonymWord>(PlayerPrefs.GetString("SynonymWordsSaver"));
                if (_SynonymWords == null) _SynonymWords = new List<SynonymWord>();
            }
            return _SynonymWords;
        }
    }
    private static TList<SynonymWord> _SynonymWords;

    public static TList<string> SynonymOf(Content Content) => SynonymOf(Content.EnglishSource);
    public static TList<string> SynonymOf(string Source)
    {
        SynonymWord dd = SynonymWords.Find(d => d.Source == Source);
        if (dd == null) return new TList<string>();
        return dd.Synonyms;
    }

    public static void AddSynonym(string Source, string Synonyms) => AddSynonym(Source, new TList<string>(Synonyms));
    public static void AddSynonym(string Source, TList<string> Synonyms)
    {
        Source = Source.ToUpper();
        for (int i = 0; i < Synonyms.Count; i++) Synonyms[i] = Synonyms[i].ToUpper();
        SynonymWord SynonymWord = SynonymWords.Find(d => d.Source == Source);
        if (SynonymWord == null) { SynonymWords.Add(new SynonymWord() { Source = Source, Synonyms = Synonyms }); return; }
        Synonyms.ForEach(d => { if (!SynonymWord.Synonyms.Contains(d)) SynonymWord.Synonyms.Add(d); });
    }
    public static void RemoveOpposite() => throw new System.Exception(" Not yet ");



